/*
 * Control functions
 */
static  bool pci_udn_can_send(struct pci_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t pci_udn_register_send(struct pci_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, pci_udn_can_send(b)));
}

static  void pci_udn_default_error_handler(struct pci_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated pci udn binding (default handler)");
    abort();
}

static  errval_t pci_udn_change_waitset(struct pci_binding *_binding, struct waitset *ws)
{
    struct pci_udn_binding *b = (void *)(_binding);
    errval_t err;
    
    // change waitset on private monitor binding if we have one
    if ((((b->udn_state).chan).monitor_binding) != get_monitor_binding()) {
        err = flounder_support_change_monitor_waitset(((b->udn_state).chan).monitor_binding, ws);
        if (err_is_fail(err)) {
            return(err_push(err, FLOUNDER_ERR_CHANGE_MONITOR_WAITSET));
        }
    }
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // re-register for receive (if previously registered)
    err = udn_chan_deregister_recv(&((b->udn_state).chan));
    if (err_is_fail(err) && (err_no(err) != LIB_ERR_CHAN_NOT_REGISTERED)) {
        return(err_push(err, LIB_ERR_CHAN_DEREGISTER_RECV));
    }
    if (err_is_ok(err)) {
        err = udn_chan_register_recv(&((b->udn_state).chan), _binding->waitset, (struct event_closure){  .handler = pci_udn_rx_handler,  .arg = _binding });
        if (err_is_fail(err)) {
            return(err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
        }
    }
    return(SYS_ERR_OK);
}

static  errval_t pci_udn_control(struct pci_binding *_binding, idc_control_t control)
{
    // no control flags are supported
    return(SYS_ERR_OK);
}


/*
 * Function to destroy the binding state
 */
 void pci_udn_destroy(struct pci_udn_binding *b)
{
    flounder_support_waitset_chanstate_destroy(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_destroy(&((b->b).tx_cont_chanstate));
    udn_chan_destroy(&((b->udn_state).chan));
}


/*
 * Bind function
 */
static  void pci_udn_bind_continuation(void *st, errval_t err, struct udn_chan *chan, struct capref notify_cap)
{
    struct pci_binding *_binding = st;
    struct pci_udn_binding *b = st;
    
    if (err_is_ok(err)) {
        // notify cap ignored
        // setup cap handlers
        (((b->udn_state).chan).cap_handlers).st = b;
        (((b->udn_state).chan).cap_handlers).cap_receive_handler = pci_udn_cap_rx_handler;
        // register for receive notification
        err = udn_chan_register_recv(&((b->udn_state).chan), _binding->waitset, (struct event_closure){  .handler = pci_udn_rx_handler,  .arg = _binding });
        if (err_is_fail(err)) {
            (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
        }
    } else {
        pci_udn_destroy(b);
    }
    
    (_binding->bind_cont)(_binding->st, err, _binding);
}

 errval_t pci_udn_init(struct pci_udn_binding *b, struct waitset *waitset, coreid_t dest_core_id, uint32_t out_chan_id, uint8_t dest_asid)
{
    errval_t err;
    struct pci_binding *_binding = &(b->b);
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = pci_udn_can_send;
    (b->b).register_send = pci_udn_register_send;
    (b->b).error_handler = pci_udn_default_error_handler;
    (b->b).tx_vtbl = pci_udn_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_udn_state_init(&(b->udn_state), b);
    err = udn_chan_init(&((b->udn_state).chan), dest_core_id, out_chan_id, dest_asid);
    if (err_is_fail(err)) {
        pci_udn_destroy(b);
        return(err_push(err, LIB_ERR_UDN_CHAN_INIT));
    }
    
    (b->b).change_waitset = pci_udn_change_waitset;
    (b->b).control = pci_udn_control;
    // register for receive notification
    err = udn_chan_register_recv(&((b->udn_state).chan), _binding->waitset, (struct event_closure){  .handler = pci_udn_rx_handler,  .arg = _binding });
    if (err_is_fail(err)) {
        (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
    }
    
    return(err);
}

static  void pci_udn_new_monitor_binding_continuation(void *st, errval_t err, struct monitor_binding *monitor_binding)
{
    struct pci_binding *_binding = st;
    struct pci_udn_binding *b = st;
    
    if (err_is_fail(err)) {
        err = err_push(err, LIB_ERR_MONITOR_CLIENT_BIND);
        goto out;
    }
    
    ((b->udn_state).chan).monitor_binding = monitor_binding;
    // start the bind on the new monitor binding
    err = udn_chan_bind(&((b->udn_state).chan), (struct udn_bind_continuation){  .handler = pci_udn_bind_continuation,  .st = b }, &(_binding->event_qnode), b->iref, monitor_binding, NULL_CAP);
    
    out:
    if (err_is_fail(err)) {
        (_binding->bind_cont)(_binding->st, err, _binding);
        pci_udn_destroy(b);
    }
}

 errval_t pci_udn_bind(struct pci_udn_binding *b, iref_t iref, pci_bind_continuation_fn *_continuation, void *st, struct waitset *waitset, idc_bind_flags_t flags)
{
    errval_t err;
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = pci_udn_can_send;
    (b->b).register_send = pci_udn_register_send;
    (b->b).error_handler = pci_udn_default_error_handler;
    (b->b).tx_vtbl = pci_udn_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_udn_state_init(&(b->udn_state), b);
    (b->b).change_waitset = pci_udn_change_waitset;
    (b->b).control = pci_udn_control;
    (b->b).st = st;
    (b->b).bind_cont = _continuation;
    b->iref = iref;
    
    // do we need a new monitor binding?
    if (flags & IDC_BIND_FLAG_RPC_CAP_TRANSFER) {
        err = monitor_client_new_binding(pci_udn_new_monitor_binding_continuation, b, waitset, DEFAULT_LMP_BUF_WORDS);
    } else {
        err = udn_chan_bind(&((b->udn_state).chan), (struct udn_bind_continuation){  .handler = pci_udn_bind_continuation,  .st = b }, &((b->b).event_qnode), iref, get_monitor_binding(), NULL_CAP);
    }
    
    if (err_is_fail(err)) {
        pci_udn_destroy(b);
    }
    return(err);
}


/*
 * Connect callback for export
 */
 errval_t pci_udn_connect_handler(void *st, struct monitor_binding *mb, uintptr_t mon_id, struct capref notify_cap, coreid_t dest_core_id, uint32_t out_chan_id, uint8_t dest_asid)
{
    struct pci_export *e = st;
    errval_t err;
    
    // allocate storage for binding
    struct pci_udn_binding *b = malloc(sizeof(struct pci_udn_binding ));
    if (b == NULL) {
        return(LIB_ERR_MALLOC_FAIL);
    }
    
    struct pci_binding *_binding = &(b->b);
    (b->b).st = NULL;
    (b->b).waitset = (e->waitset);
    event_mutex_init(&((b->b).mutex), e->waitset);
    (b->b).can_send = pci_udn_can_send;
    (b->b).register_send = pci_udn_register_send;
    (b->b).error_handler = pci_udn_default_error_handler;
    (b->b).tx_vtbl = pci_udn_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_udn_state_init(&(b->udn_state), b);
    (b->b).change_waitset = pci_udn_change_waitset;
    (b->b).control = pci_udn_control;
    
    // run user's connect handler
    err = ((e->connect_cb)(e->st, _binding));
    if (err_is_fail(err)) {
        // connection refused
        pci_udn_destroy(b);
        return(err);
    }
    
    // accept the connection and setup the channel
    err = udn_chan_accept(&((b->udn_state).chan), mon_id, dest_core_id, out_chan_id, dest_asid);
    if (err_is_fail(err)) {
        err = err_push(err, LIB_ERR_UDN_CHAN_ACCEPT);
        (_binding->error_handler)(_binding, err);
        return(err);
    }
    
    // notify cap ignored
    // setup cap handlers
    (((b->udn_state).chan).cap_handlers).st = b;
    (((b->udn_state).chan).cap_handlers).cap_receive_handler = pci_udn_cap_rx_handler;
    
    // register for receive notification
    err = udn_chan_register_recv(&((b->udn_state).chan), _binding->waitset, (struct event_closure){  .handler = pci_udn_rx_handler,  .arg = _binding });
    if (err_is_fail(err)) {
        (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
    }
    
    // send back bind reply
    udn_chan_send_bind_reply(mb, &((b->udn_state).chan), SYS_ERR_OK, mon_id, NULL_CAP);
    
    return(SYS_ERR_OK);
}

