/*
 * Control functions
 */
static  bool trivfs_lmp_can_send(struct trivfs_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t trivfs_lmp_register_send(struct trivfs_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, trivfs_lmp_can_send(b)));
}

