/*
 * Control functions
 */
static  bool net_soft_filters_lmp_can_send(struct net_soft_filters_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t net_soft_filters_lmp_register_send(struct net_soft_filters_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, net_soft_filters_lmp_can_send(b)));
}

static  void net_soft_filters_lmp_default_error_handler(struct net_soft_filters_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated net_soft_filters lmp binding (default handler)");
    abort();
}

static  errval_t net_soft_filters_lmp_change_waitset(struct net_soft_filters_binding *_binding, struct waitset *ws)
{
    struct net_soft_filters_lmp_binding *b = (void *)(_binding);
    
    // Migrate register and TX continuation notifications
    flounder_support_migrate_notify(&(_binding->register_chanstate), ws);
    flounder_support_migrate_notify(&(_binding->tx_cont_chanstate), ws);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // Migrate send and receive notifications
    lmp_chan_migrate_recv(&(b->chan), ws);
    lmp_chan_migrate_send(&(b->chan), ws);
    
    return(SYS_ERR_OK);
}

static  errval_t net_soft_filters_lmp_control(struct net_soft_filters_binding *_binding, idc_control_t control)
{
    struct net_soft_filters_lmp_binding *b = (void *)(_binding);
    
    b->flags = idc_control_to_lmp_flags(control, b->flags);
    
    return(SYS_ERR_OK);
}

/*
 * Functions to initialise/destroy the binding state
 */
