/*
 * Capability sender function
 */
static  void net_soft_filters_udn_cap_send_handler(void *arg)
{
    // Get the binding state from our argument pointer
    struct net_soft_filters_binding *_binding = arg;
    struct net_soft_filters_udn_binding *b = arg;
    errval_t err;
    err = SYS_ERR_OK;
    
    assert(((b->udn_state).capst).rx_cap_ack);
    assert(((b->udn_state).capst).monitor_mutex_held);
    
    // Switch on current outgoing message
    switch (_binding->tx_msgnum) {
    case net_soft_filters_register_filter_memory_request__msgnum:
        // Switch on current outgoing cap
        switch (((b->udn_state).capst).tx_capnum) {
        case 0:
            err = flounder_stub_send_cap(&((b->udn_state).capst), ((b->udn_state).chan).monitor_binding, ((b->udn_state).chan).monitor_id, ((_binding->tx_union).register_filter_memory_request).mem, false, net_soft_filters_udn_cap_send_handler);
            if (err_is_fail(err)) {
                (_binding->error_handler)(_binding, err);
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->register_chanstate));
                flounder_support_deregister_chan(&(_binding->tx_cont_chanstate));
                break;
            }
            break;
        case 1:
            flounder_support_monitor_mutex_unlock(((b->udn_state).chan).monitor_binding);
            if ((_binding->tx_msg_fragment) == 1) {
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


