/*
 * Cap send/receive handlers
 */
static  void net_soft_filters_udn_cap_rx_handler(void *arg, errval_t success, struct capref cap, uint32_t capid)
{
    // Get the binding state from our argument pointer
    struct net_soft_filters_binding *_binding = arg;
    struct net_soft_filters_udn_binding *b = arg;
    errval_t err;
    err = SYS_ERR_OK;
    
    assert(capid == (((b->udn_state).capst).rx_capnum));
    
    // Check if there's an associated error
    // FIXME: how should we report this to the user? at present we just deliver a NULL capref
    if (err_is_fail(success)) {
        DEBUG_ERR(err, "error in cap transfer");
    }
    
    // Switch on current incoming message
    switch (_binding->rx_msgnum) {
    case net_soft_filters_register_filter_memory_request__msgnum:
        // Switch on current incoming cap
        switch ((((b->udn_state).capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).register_filter_memory_request).mem = cap;
            if ((_binding->rx_msg_fragment) == 1) {
                FL_DEBUG("udn RX net_soft_filters.register_filter_memory_request\n");
                assert(((_binding->rx_vtbl).register_filter_memory_request) != NULL);
                ((_binding->rx_vtbl).register_filter_memory_request)(_binding, ((_binding->rx_union).register_filter_memory_request).mem);
                _binding->rx_msgnum = 0;
                // register for receive notification
                err = udn_chan_register_recv(&((b->udn_state).chan), _binding->waitset, (struct event_closure){  .handler = net_soft_filters_udn_rx_handler,  .arg = _binding });
                if (err_is_fail(err)) {
                    (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
                }
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


/*
 * Monitor mutex acquire continuation
 */
static  void net_soft_filters_udn_monitor_mutex_cont(void *arg)
{
    struct net_soft_filters_udn_binding *b = arg;
    assert(!(((b->udn_state).capst).monitor_mutex_held));
    ((b->udn_state).capst).monitor_mutex_held = true;
    if (((b->udn_state).capst).rx_cap_ack) {
        net_soft_filters_udn_cap_send_handler(b);
    }
}


/*
 * Message sender functions
 */
static  errval_t net_soft_filters_register_filter_memory_request__udn_send(struct net_soft_filters_binding *_binding, struct event_closure _continuation, struct capref mem)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = net_soft_filters_register_filter_memory_request__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).register_filter_memory_request).mem = mem;
    FL_DEBUG("udn TX net_soft_filters.register_filter_memory_request\n");
    
    // init cap send state
    ((((struct net_soft_filters_udn_binding *)(_binding))->udn_state).capst).tx_capnum = 0;
    ((((struct net_soft_filters_udn_binding *)(_binding))->udn_state).capst).rx_cap_ack = false;
    ((((struct net_soft_filters_udn_binding *)(_binding))->udn_state).capst).monitor_mutex_held = false;
    
    // wait to acquire the monitor binding mutex
    flounder_support_monitor_mutex_enqueue(((((struct net_soft_filters_udn_binding *)(_binding))->udn_state).chan).monitor_binding, &(_binding->event_qnode), (struct event_closure){  .handler = net_soft_filters_udn_monitor_mutex_cont,  .arg = _binding });
    
    // try to send!
    net_soft_filters_udn_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

