static  errval_t net_soft_filters_pause__udn_send(struct net_soft_filters_binding *_binding, struct event_closure _continuation, uint64_t filter_id, uint64_t buffer_id_rx, uint64_t buffer_id_tx)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = net_soft_filters_pause__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).pause).filter_id = filter_id;
    ((_binding->tx_union).pause).buffer_id_rx = buffer_id_rx;
    ((_binding->tx_union).pause).buffer_id_tx = buffer_id_tx;
    FL_DEBUG("udn TX net_soft_filters.pause\n");
    
    // try to send!
    net_soft_filters_udn_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

